import { db } from '@/main'
export default {
  data: () => ({
    today: new Date().toISOString().substr(0, 10),
    focus: new Date().toISOString().substr(0, 10),
    type: 'month',
    typeToLabel: {
      month: 'Month',
      week: 'Week',
      day: 'Day',
      '4day': '4 Days',
    },
    name: null,
    details: null,
    start: null,
    // start: null,
    startTime:null,
    endTime:null,
    //end: null,
    color: '#1976D2', // default event color
    currentlyEditing: null,
    selectedEvent: {},
    selectedElement: null,
    selectedOpen: false,
    events: [],
    dialog: false,
  }),
  mounted () {
    this.getEvents()
  },
  computed: {
    title () {
      const { start, end } = this
      if (!start || !end) {
        return ''
      }
      const startMonth = this.monthFormatter(start)
      const endMonth = this.monthFormatter(end)
      const suffixMonth = startMonth === endMonth ? '' : endMonth
      const startYear = start.year
      const endYear = end.year
      const suffixYear = startYear === endYear ? '' : endYear
      const startDay = start.day + this.nth(start.day)
      const endDay = end.day + this.nth(end.day)
      switch (this.type) {
        case 'month':
        return `${startMonth} ${startYear}`
        case 'week':
        case '4day':
        return `${startMonth} ${startDay} ${startYear} - ${suffixMonth} ${endDay} ${suffixYear}`
        case 'day':
        return `${startMonth} ${startDay} ${startYear}`
      }
      return ''
    },
    monthFormatter () {
      return this.$refs.calendar.getFormatter({
        timeZone: 'UTC', month: 'long',
      })
    }
  },
  methods: {
    async getEvents () {
      let snapshot = await db.collection('calEvent').get()
      let events = []
      snapshot.forEach(doc => {
        let appData = doc.data()
        console.log(appData)
        appData.id = doc.id
        events.push(appData)
      })
      this.events = events
    },
    viewDay ({ date }) {
      this.focus = date
      this.type = 'day'
    },
    getEventColor (event) {
      return event.color
    },
    setToday () {
      this.focus = this.today
    },
    prev () {
      this.$refs.calendar.prev()
    },
    next () {
      this.$refs.calendar.next()
    },
    checkAvailibility(date,start,end) {

      var av = 1
      this.events.forEach(ev => {
       if(ev.start == date) {
         if((ev.startTime >= start && ev.startTime <= end) || (ev.endTime >= start && ev.startTime <= end)) {
           av=0
         }
       }

      })
      return av
    },
    async addEvent () {

      let availibility = this.checkAvailibility(this.start,this.startTime, this.endTime)
      console.log(availibility)
      if (availibility == 1) {
        if (this.name && this.start && this.startTime <= "16:00" && this.endTime <= "17:00") {
          await db.collection("calEvent").add({
            name: this.name,
            details: this.details,
            start: this.start,
            startTime:this.startTime,
            endTime: this.endTime,
            //start: this.start,
            //end: this.end,
            color: this.color
          })
          this.getEvents()
          this.name = '',
          this.details = '',
          this.start = '',
          //this.start = '',
          this.startTime = '',
          this.endTime = '',
          //this.end = '',
          this.color = ''
        } 

        else if(this.name && this.start && this.startTime > "16:00" && this.endTime > "17:00") {
          this.getEvents()
          alert('Apointments after 4 pm are not allowed. ')
        }

        else {
          this.getEvents()
          alert('You must enter event name, start, and end time')
        }

      }
      else
      {
        this.getEvents()

          this.start = '',
          //this.start = '',
          this.startTime = '',
          this.endTime = '',
          //this.end = '',

        alert('This time slot is not available.')

      }

    },
    editEvent (ev) {
      this.currentlyEditing = ev.id
    },
    async updateEvent (ev) {
      await db.collection('calEvent').doc(this.currentlyEditing).update({
        details: ev.details
      })
      this.selectedOpen = false,
      this.currentlyEditing = null
    },
    async deleteEvent (ev) {
      await db.collection("calEvent").doc(ev).delete()
      this.selectedOpen = false,
      this.getEvents()
    },
    showEvent ({ nativeEvent, event }) {
      const open = () => {
        this.selectedEvent = event
        this.selectedElement = nativeEvent.target
        setTimeout(() => this.selectedOpen = true, 10)
      }
      if (this.selectedOpen) {
        this.selectedOpen = false
        setTimeout(open, 10)
      } else {
        open()
      }
      nativeEvent.stopPropagation()
    },
    updateRange ({ start, end }) {
      this.start = start
      this.end = end
    },
    nth (d) {
      return d > 3 && d < 21
      ? 'th'
      : ['th', 'st', 'nd', 'rd', 'th', 'th', 'th', 'th', 'th', 'th'][d % 10]
    }
  }
}
